import React, {Component} from 'react';
import { MIN_STR, MAX_STR, MIN_INT, MAX_INT, MIN_EDU, MAX_EDU } from './constant'

class CharStatus extends Component{
    constructor(){
        super();
    }

    buildOptions = (min, max)=>{
        let arr = [];

        arr.push(<option key = {0} value=" "> </option>)
        for (let i = min; i <= max; i++) {
            arr.push(<option key={i} value="{i}">{i}</option>)
        }

        return arr; 
    }

    getStatus = (n, m, l) => {
        let sum = 0;
        for (let i = 1; i <= n; i++) {
            sum += Math.floor(Math.random() * m) + 1;
        }

        sum += l;
        console.log(sum);
        return sum;
    }

    render(){
        return(
            <div>
                <table>
                    <tbody>
                        <tr>
                            <td></td>
                            <td>STR</td>
                            <td>POW</td>
                            <td>DEX</td>
                            <td>APP</td>
                            <td>SIZ</td>
                            <td>INT</td>
                            <td>EDU</td>
                            <td>HP</td>
                            <td>MP</td>
                            <td>初期SAN</td>
                            <td>アイデア</td>
                            <td>幸運</td>
                            <td>知識</td>
                        </tr>
                        <tr>
                            <td>能力値</td>
                            <td>
                                <select>
                                    {this.buildOptions(MIN_STR, MAX_STR)}
                                </select>
                            </td>
                            <td>
                                <select>
                                    {this.buildOptions(MIN_STR, MAX_STR)}
                                </select>
                            </td>
                            <td>
                                <select>
                                    {this.buildOptions(MIN_STR, MAX_STR)}
                                </select>
                            </td>
                            <td>
                                <select>
                                    {this.buildOptions(MIN_STR, MAX_STR)}
                                </select>
                            </td>
                            <td>
                                <select>
                                    {this.buildOptions(MIN_STR, MAX_STR)}
                                </select>
                            </td>
                            <td>
                                <select>
                                    {this.buildOptions(MIN_INT, MAX_INT)}
                                </select>
                            </td>
                            <td>
                                <select>
                                    {this.buildOptions(MIN_EDU, MAX_EDU)}
                                </select>
                            </td>
                        </tr>
                        <tr>
                            <td>その他増加分</td>
                        </tr>
                        <tr>
                            <td>一時的増加</td>
                        </tr>
                    </tbody>
                </table>

                <button onClick={()=>this.getStatus(3,6,0)}>キャラクター作成</button>
            </div>
        )
    }

}

export default CharStatus;
