import React, {Component} from 'react';
import { MIN_STR, MAX_STR } from './constants'

class CharStatus extends Component{
    constructor(){
        super();
    }

    buildOptions() {
        var arr = [];

        for (let i = 1; i <= 10; i++) {
            arr.push(<option key={i} value="{i}">{i}</option>)
        }

        return arr; 
    }

    render(){
        return(
            <table>
                <tr>
                    <td></td>
                    <td>STR</td>
                    <td>POW</td>
                    <td>DEX</td>
                    <td>APP</td>
                    <td>SIZ</td>
                    <td>INT</td>
                    <td>EDU</td>
                    <td>HP</td>
                    <td>MP</td>
                    <td>初期SAN</td>
                    <td>アイデア</td>
                    <td>幸運</td>
                    <td>知識</td>
                </tr>
                <tr>
                    <td>能力値</td>
                    <td>
                        <select>
                            {range(MIN_STR, MAX_STR + 1).map(value => <option key={value} value={value}>{value}</option>) }
                        </select>
                    </td>
                    <td><select /></td>
                    <td><select /></td>
                    <td><select /></td>
                    <td><select /></td>
                    <td><select /></td>
                    <td><select /></td>
                </tr>
                <tr>
                    <td>その他増加分</td>
                </tr>
                <tr>
                    <td>一時的増加</td>
                </tr>
            </table>
        )
    }

}

export default CharStatus;
