import React, {Component} from 'react';
import { MIN_STR, MAX_STR, MIN_INT, MAX_INT, MIN_EDU, MAX_EDU } from './constant'

class CharOptions extends Component{
    constructor(){
        super();
    }

    buildOptions = (status)=>{
        let arr = [];
        let min = MIN_STR;
        let max = MAX_STR

        if (status==='int'){
            min = MIN_INT;
            max = MAX_INT;
        } else if (status==='edu'){
            min = MIN_EDU;
            max = MAX_EDU;
        }

        arr.push(<option key = {0} value=" "> </option>)
        for (let i = min; i <= max; i++) {
            arr.push(<option key={i} value="{i}">{i}</option>)
        }

        return arr; 
    }

    render(){
        console.log(this.props)
        return(
            <select value={this.props.value} readOnly>
                {this.buildOptions(this.props.status)}
            </select>
        )
    }

}

export default CharOptions;
