import React, {Component} from 'react';
import { MIN_STR, MAX_STR, MIN_INT, MAX_INT, MIN_EDU, MAX_EDU } from './constant'

class CharOptions extends Component{
    constructor(){
        super();
    }

    buildOptions = (min, max, status)=>{
        let arr = [];

        if (status==='int'){
            min = MIN_INT;
            max = max_INT;
        } elif (status==='int')

        arr.push(<option key = {0} value=" "> </option>)
        for (let i = min; i <= max; i++) {
            arr.push(<option key={i} value="{i}">{i}</option>)
        }

        return arr; 
    }

    render(){
        return(
            <select>
                {this.buildOptions(MIN_STR, MAX_STR, this.props.status)}
            </select>
        )
    }

}

export default CharOptions;
